INSERT INTO `article` VALUES ('504', '1524326544000', 'https://www.liuli.pl/wp/59240.html', 'http://ww1.acg.gy/005zWjpngy1fqkq1l762kj30jq0b4tcf.jpg', 'GOLD BEAR|女子高中生|学园|调教', '\n\n「フル・ライフサポータ育成計画」就是那个长得很奇怪的老师调教女学生的故事，这次是后篇。\n[MINKミンク]社的游戏《龍堂寺士門の淫謀》改编。 继续阅读 →\n', '[GOLD BEAR]龍堂寺士門の淫謀 後編 女子喰えば、喜悦なるなり龍堂寺！（1-2）', '127', '4.44', '571073075bb1ab1a5d5e049c90a390ce68a3470e');
