INSERT INTO `article` VALUES ('1140', '1597949458000', 'https://llss.pl/wp/all/anime/%e7%a3%94%ef%bc%88%e3%83%8f%e3%83%aa%e3%83%84%e3%82%b1%ef%bc%89%e5%be%8c%e7%b7%a8clone%e4%ba%ba%e9%96%93/', 'https://llss.pl/wp/wp-content/uploads/2020/07/03.jpg', 'NTR|人妻', '\n			主要讲女主三枝和理事长的儿子冈崎订婚了，三个碧池对女主三枝获得幸福不满，于是主动送了冈崎一波，然后拍照给女主看，再用照片威胁女主变成公交车的故事，看完总觉得冈崎不亏啊，一换三血赚 继续阅读 →\n					', '【熟肉】磔（ハリツケ）後編[clone人間]【附前作】', '27', '4', '939033DF8DAA4C0CCC3DBFF673B4211800606B9F');
