INSERT INTO `article` VALUES ('1142', '1597503635000', 'https://llss.pl/wp/all/anime/%e3%80%90%e7%94%9f%e8%82%89%e3%80%91ova-%e3%82%88%e3%81%86%e3%81%93%e3%81%9d%ef%bc%81-%e3%82%b9%e3%82%b1%e3%83%99%e3%82%a8%e3%83%ab%e3%83%95%e3%81%ae%e6%a3%ae%e3%81%b8-%ef%bc%833-%e3%82%a8%e3%83%ab/', 'https://llss.pl/wp/wp-content/uploads/2020/06/02-18.jpg', '人外娘|暗夜精灵|精灵|黑皮', '\n			欢迎来到浪荡的妖精之森林，剧情紧跟上集，男主也就是救世主,救世主可以拯救精灵的神树。于是黑暗精灵要抢夺救世主。男主帮助白精灵打倒黑暗精灵拯救精灵族 继续阅读 →\n					', '【熟肉】OVA ようこそ！ スケベエルフの森へ ＃3 エルフとダークエルフの全面対決！ 救世主様と『らぶらぶ子作り対決』【附前作】', '75', '4.28', '6F4B9BA004C0C50A5C4326EAA1CBC5FADCD99A99');
