INSERT INTO `article` VALUES ('1144', '1575500678000', 'https://llss.pl/wp/all/anime/%e3%80%90%e7%94%9f%e8%82%89%e3%80%91ova-%e3%82%a2%e3%83%80%e3%83%ab%e3%83%88%ef%bc%832/', 'https://llss.pl/wp/wp-content/uploads/2019/12/06-1.jpg', 'NTR|催眠|后宫|超能力|逆袭', '\n			\n故事主要讲不受待见的屌丝男主掌握了超能力逆袭，而且他的超能力还和催眠不一样，不能控制思想，只能控制物体。 继续阅读 →\n					', '【熟肉】OVA アダルト＃2【附前作】', '127', '4.45', '03CCBD6FAF26F00CE489888698A78DFB5B9B200D');
