INSERT INTO `article` VALUES ('1149', '1597590037000', 'https://llss.pl/wp/all/anime/%e3%80%90%e7%94%9f%e8%82%89%e3%80%91%e5%88%9d%e3%82%81%e3%81%a6%e3%81%ae%e3%83%92%e3%83%88%e3%83%85%e3%83%9e-%e7%ac%ac2%e8%a9%b1-%e7%b6%9a%e3%83%bb%e4%bf%ba%e3%81%8c%e8%a6%8b%e3%81%9f%e3%81%93/', 'https://llss.pl/wp/wp-content/uploads/2020/08/03-2.jpg', 'NTR', '\n			名字翻译过来是《初恋》，男主带女朋友回家见父亲，然后女主发现，男主的父亲居然是自己七年前的对象，前作介绍可以看这里 继续阅读 →\n					', '【熟肉】初めてのヒトヅマ 第2話 続・俺が見たことのない彼女', '41', '3.63', '31af0f8d4a5b68491ec0126d1ba578857d64c6f6');
INSERT INTO `article` VALUES ('1150', '1597244452000', 'https://llss.pl/wp/all/anime/%e7%90%a5%e7%8f%80%e8%89%b2%e3%81%ae%e3%83%8f%e3%83%b3%e3%82%bf%e3%83%bc-the-animation%e3%80%90pv%e3%80%91/', 'https://llss.pl/wp/wp-content/uploads/2020/07/019.jpg', '奇幻|御姐|扶她|探险|正太|猎人|西川鷹司', '\n			在遥远的宇宙中有一个惑星，它的森林里盛产一种叫ノヴァストーン的矿石，拥有巨大能量的ノヴァストーン的矿石价值千金，无数的矿石猎人蜂拥而至，女主怀着希望，也踏上了寻找矿石的旅程。 继续阅读 →\n					', '【熟肉】琥珀色のハンター THE ANIMATION', '90', '4.06', '3ba5fad2ed10ff571136564efd7d8d4d47ba6925');
