INSERT INTO `article` VALUES ('1159', '1597417253000', 'https://llss.pl/wp/all/anime/%e3%80%90%e7%94%9f%e8%82%89%e3%80%91ova-%e3%82%88%e3%81%86%e3%81%93%e3%81%9d%ef%bc%81-%e3%82%b9%e3%82%b1%e3%83%99%e3%82%a8%e3%83%ab%e3%83%95%e3%81%ae%e6%a3%ae%e3%81%b8-%ef%bc%834-%e3%82%a8%e3%83%ab/', 'https://llss.pl/wp/wp-content/uploads/2020/07/03-11.jpg', '后宫|精灵|雷火剣', '\n			欢迎来到浪荡的妖精之森林，剧情紧跟上集，因为男主的来到，持续千年的精灵和暗夜精灵的战争终于和平结束，精灵族实现了统一，既然没有战争了，那么种族繁荣就是精灵族的首要课题了，前作介绍可以看这里 继续阅读 →\n					', '【熟肉】OVA ようこそ！ スケベエルフの森へ ＃4 エルフもダークエルフも仲良く子作り！ 救世主様と『ハーレム生活』', '73', '4.3', '45909ee4d155f07a86e59bf41804cc18f9eb85b5');
