INSERT INTO `article` VALUES ('1281', '1576916167000', 'https://llss.pl/wp/all/anime/%e3%80%90%e7%94%9f%e8%82%89%e3%80%91%e3%83%9c%e3%82%af%e3%81%a8%e5%bd%bc%e5%a5%b3%ef%bc%88%e5%a5%b3%e5%8c%bb%ef%bc%89%e3%81%ae%e8%a8%ba%e5%af%9f%e6%97%a5%e8%aa%8c-the-animation/', 'https://llss.pl/wp/wp-content/uploads/2019/12/06-6.jpg', '医生|后宫|护士|百合|纯爱', '\n			我发现日本人医疗水平之低下….连听诊器这个医疗器械的使用都有着深深的误解，我感觉历史的使命感在鞭策我，要我去日本教导这些落后的医生正确的使用医疗器械…. 继续阅读 →\n					', '【熟肉】ボクと彼女（女医）の診察日誌 THE ANIMATION【附前作】', '147', '4.26', '4fde1d42fd37c3163f0a860e459063cca0bc60b0');
