INSERT INTO `article` VALUES ('1336', '1556639499000', 'https://llss.pl/wp/all/anime/%e3%82%a8%e3%83%ad%e3%82%b3%e3%83%b3%e3%83%93%e3%83%8b%e5%ba%97%e9%95%b7-%e3%83%8a%e3%83%9e%e3%82%a4%e3%82%ad%e9%81%8e%e5%89%b0%e8%87%aa%e6%84%8f%e8%ad%98%e5%a8%98%e3%83%bb%e7%be%8e%e6%a8%b9-%ef%bd%9e/', 'https://llss.pl/wp/wp-content/uploads/2019/04/01-11.jpg', 'PoRO', '\n			エロコンビニ店長系列第8话，这个系列是从2016年开始发售的，PoRO除了鬼父外就这个系列还算可以，画风也很不错。本集主要是讲工口店长去连锁的其他店铺当一个月临时店长，遇到一个JK店员。  继续阅读 →\n					', '【熟】エロコンビニ店長 ナマイキ過剰自意識娘・美樹 ～私にちたくせにっ～【附前作】', '44', '4.23', 'c3ca859384fad4c32bc0c30d41056c68b61e7ae1');
