INSERT INTO `article` VALUES ('1418', '1541973341000', 'https://llss.pl/wp/all/anime/%e3%80%90%e7%86%9f%e8%82%89%e3%80%91%e7%b5%82%e9%9b%bb%e5%be%8c-%e3%82%ab%e3%83%97%e3%82%bb%e3%83%ab%e3%83%9b%e3%83%86%e3%83%ab%e3%81%a7-%e4%b8%8a%e5%8f%b8%e3%81%ab%e5%be%ae%e7%86%b1%e4%bc%9d-3/', 'https://llss.pl/wp/wp-content/uploads/2018/11/1-9.jpg', '女性向', '\n			\n这一话讲女主和后宫黄毛逛街，差点被车撞了。黄毛救了她之后，展现了一下花脸，看来她对黄毛有点动心，然后剧情回到公司，棕毛男主感冒了，女主又去男主家照顾男主…… 继续阅读 →\n					', '【熟】終電後, カプセルホテルで, 上司に微熱伝わる夜【06】', '32', '3.84', '2bbd5e6d62fa99fe7ecc15cbcc6424e1e2c8794a');
