INSERT INTO `article` VALUES ('1445', '1543258696000', 'https://llss.pl/wp/all/anime/%e3%80%90%e7%94%9f%e8%82%89%e3%80%91tinderbox-2/', 'https://llss.pl/wp/wp-content/uploads/2018/11/01-11.jpg', 'NTR', '\n			\n故事讲橘毛（就是上图这个），喜欢男主，结果发现男主已经有女朋友了，正在伤心的时候，被妖艳贱货（黄毛）拖下水，自暴自弃，最后再把男主的女朋友（棕毛）拖下水。讲道理，开始我还以为是纯爱后宫，结果看到后面就是NTR。 继续阅读 →\n					', '【熟肉】Tinderbox 2 V2', '45', '3.56', '097e3ff2f83e82401c455e14012edcee7be73f60');
