INSERT INTO `article` VALUES ('1450', '1543788130000', 'https://llss.pl/wp/all/anime/2018%e5%b9%b412%e6%9c%88%e9%87%8c%e7%95%aa%e9%a2%84%e5%91%8a%e4%b8%8e%e8%b0%83%e6%9f%a5/', 'https://llss.pl/wp/wp-content/uploads/2018/12/02-3.jpg', '调查|预告', '\n			\n12月简直是豪华阵容，让人热血沸腾，有为了守护世界不被魔王入侵的女勇者；有为了理想坚守道场的女武师；更有战败国忠义的女战士；有异世界邪恶的暗夜灵…..\n 继续阅读 →\n					', '2018年12月里番预告与调查', '48', '4.73', '');
