INSERT INTO `article` VALUES ('1571', '1522529390000', 'https://llss.pl/wp/all/anime/%e3%83%89s%e3%81%aa%e3%83%9e%e3%82%a4%e3%83%8a%e4%bc%9a%e9%95%b7%e3%82%b5%e3%83%9e%e3%81%8cm%e3%83%8e%e3%83%bc%e3%83%88%e3%81%ab%e6%94%af%e9%85%8d%e3%81%95%e3%82%8c%e3%81%be%e3%81%97%e3%81%9f-2/', 'https://llss.pl/wp/wp-content/uploads/2018/03/4-5.jpg', 'PoRO|抖M|生徒会長|足控', '\n			\n爱莉长大上高中之后，变成了孤高的金发学生会会长，欺负男主，下场可想而知…（好吧，我编不下去了，你们自己看吧） 继续阅读 →\n					', '【熟】ドSなマイナ会長サマがMノートに支配されました。～ドSな会長サマの躾け do M【附前作】', '41', '4.1', 'a52c1dbbdb31b73d9275ca681c061f77f2e2e712|8960406B274A553AB438218FA5092093EFAB03E3');
INSERT INTO `article` VALUES ('1572', '1511552089000', 'https://llss.pl/wp/all/anime/implicity-ii-%e6%9d%b1%e5%b1%b1%e7%bf%94/', 'https://hacg.cool/wp/wp-content/uploads/2017/10/2-3.jpg', 'double penetration|gag|未来|東山翔|生化人', '\n			\n漫改本，世界漫画史上最好的反乌托邦巨作《Implicity》，故事发生在22世纪，人类已经抛弃了公元纪年，世界分为地上和地下两大区域 （都在一个保护罩内，有可能是宇宙殖民飞船） ，多数人类生活在地上（故事中称为CITY），地下则是工业和农业工厂以及各种服务业的所在地（这个设定在日本是因《铳梦》而流行的）。 继续阅读 →\n					', '【熟】Implicity II [東山翔] V2', '26', '4.12', '7BD9EE8E99913E89C47AC09324AD8AA7FA74B415|FF409E632BCF0C0D92AF880C75A09BD98CFAF927|996DD2670C20CB53337E1E156B7AB40456403B4C');
