INSERT INTO `article` VALUES ('1606', '1514091520000', 'https://llss.pl/wp/all/anime/%e7%89%87%e7%94%b0%e8%88%8e%e3%81%ab%e5%ab%81%e3%81%84%e3%81%a7%e3%81%8d%e3%81%9f%e2%97%8b%e3%82%b7%e3%82%a2%e5%a8%98%e3%81%a8h%e3%81%97%e3%81%be%e3%81%8f%e3%82%8b%e3%81%8a%e8%a9%b1-%e8%8b%a5-2/', 'https://hacg.cool/wp/wp-content/uploads/2017/10/1-6.jpg', 'collaborationworks|人妻|外国人|欧派', '\n			\n找不到工作后回到父母家的鈴村健汰和弟弟的外国人妻阿莉萨的故事（弟弟长期商务旅行去了），嗯，アリサ应该就是翻译成阿莉萨吧，总之，再说一次我不喜欢这种毫不协调的欧派，人类是不可能长成这样的！！！ 继续阅读 →\n					', '【熟】片田舎に嫁いできた○シア娘とHしまくるお話 若奥様はお祭りに行きたいの巻【附前作】', '90', '4.18', '44bf58186c4f8b54fa0a9ae3de84f1b34fb79bf0');
