INSERT INTO `article` VALUES ('1622', '1519667971000', 'https://llss.pl/wp/all/anime/%e5%90%8c%e4%ba%ba%e3%82%a2%e3%83%8b%e3%83%a1a-third-dimension-atd-works01-chris-editionvr-ver-170202/', 'https://wx4.sinaimg.cn/mw690/006GutMagy1foqtcykzjxj31kw1kwe32.jpg', '3D动画|swf|同人动画|资料集', '\n			\n这个也算是老物了，作者现在的人物建模可以说是最终幻想级别的了，不过说是动画大部分都是有声音的gif也就是swf文件，可以看上五分钟的只有两个，或许，这就是艺术吧（读）。作者是个P站的老咸鱼，现在也依然在勤勉的更新gif，这个其实也就相当于他的作品集有声化加特典。 继续阅读 →\n					', '(同人アニメ)[A THIRD DIMENSION] ATD WORKS01 “CHRIS EDITION”+VR (Ver.170202)', '119', '4.21', '86cdbc52f8d03b803de571cfea02b2f3fb26e62a');
