INSERT INTO `article` VALUES ('1696', '1529697310000', 'https://llss.pl/wp/all/anime/%e3%81%9b%e3%82%8b%e3%81%b5%e3%81%83%e3%81%a3%e3%81%97%e3%82%85lilitales-%e3%83%aa%e3%83%aa%e3%83%86%e3%82%a4%e3%83%ab%e3%82%ba-act3-%e9%ad%94%e3%81%ae%e8%a1%80%e8%84%88/', 'https://llss.pl/wp/wp-content/uploads/2018/06/5-5.jpg', '游戏改编|牧师|王女|盗贼|银骑士', '\n			\n一个正统王道RPG游戏改编的里番，角色有姬骑士、盗贼、牧师+王女，上一话讲王女吉儿及禁卫军被强盗袭击团灭，吉儿被盗强盗获，姬骑士露娜利耶为了救回王女独自前往复仇及拯救王女，结果被抓，然后牧师又去救露娜利耶，这一话讲牧师被抓，盗贼又去救露娜利耶和牧师，讲道理，这种毫无根据的自信是那里来的？ 继续阅读 →\n					', '【熟】[せるふぃっしゅ]Lilitales -リリテイルズ act3 魔の血脈【附前作】', '53', '4.51', '5RLXPRZOSDHL3EZ3Z4HI4RP6JUCYVSRN|PCBGQK3POPEGG5KBU276CNDDBZEVBNLV');
