INSERT INTO `article` VALUES ('1719', '1529697370000', 'https://llss.pl/wp/all/anime/%e3%80%90%e7%86%9f%e8%82%89%e3%80%91%e4%b8%80%e6%b1%82ru%e9%ad%82-%e7%95%aa%e5%a4%96%e7%b7%a8%e3%81%94%e3%81%b0%e3%82%93/', 'https://llss.pl/wp/wp-content/uploads/2018/05/2-12.jpg', '后宫|学园|排球社', '\n			\n故事背景是男主偷了排球部的衣服，然后被迫成为排球部经理的事情。\n我要去当这种排球部的经理，真好….(基本和前作比就是换发型及颜色，其他一样) 继续阅读 →\n					', '【熟】一求RU魂 番外編[ごばん]【附前作】', '60', '4.25', 'C149B536544D6DE5440F93BF2295D8B3E7DDC259');
