INSERT INTO `article` VALUES ('1774', '1493405078000', 'https://llss.pl/wp/all/anime/%e7%86%9f%e8%82%89%e3%83%8c%e3%82%ad%e3%81%a9%e3%81%8d%e3%83%83%ef%bc%81revolution%ef%bd%9e%e5%a4%a9%e4%bd%bf%e3%81%a8%e6%82%aa%e9%ad%94%e3%81%ae%e6%90%be%e7%b2%be%e3%83%90%e3%83%88%e3%83%ab/', 'https://llss.pl/wp/wp-content/uploads/2017/02/7.jpg', '天使|恶魔|渣男', '\n			\n 上集讲~男主在打球的时候，因为自己懒得不想打了，就在旁观~\n女主过来搭话的时候，男主挺身而出邦女主挡了一球晕过去了~懦弱的他被天使选中\n然后在外，男主和女主被控制住，即将发生羞羞的事情~突然就完了，尼玛，就这样就完了？？？\n这一集居然又空大，女主没有一血，男主居然被拿一血，这不科学！！~ヽ(=^･ω･^=)丿 继续阅读 →\n					', '[熟]ヌキどきッ！Revolution～天使と悪魔のバトル～ 夕焼け空に天使降臨！ セラのおっぱい、自由にしてね 編', '51', '4.31', 'e9aec16e9c399b4a90daf15d41a075dd565986a0');
