INSERT INTO `article` VALUES ('1842', '1480427861000', 'https://llss.pl/wp/all/anime/%e6%a1%9c%e9%83%bd%e5%ad%97%e5%b9%95%e7%bb%84%e3%83%a1%e3%83%aa%e3%83%bc%e3%83%bb%e3%82%b8%e3%82%a7%e3%83%bc%e3%83%b3%e9%83%bd%e5%b8%82%e4%bc%9d%e8%aa%ac%e3%82%b7%e3%83%aa%e3%83%bc%e3%82%ba/', 'https://llss.pl/wp/wp-content/uploads/2016/11/c927151sample17.jpg', 'メリー・ジェーン|桜都字幕组', '\n			\n其实……这番也是正太番~而且……也是讲得八尺女~\n不过这部就没那么重口就是了，感觉是我喜欢的类型~\n这系列现在已经是第三话了吧~<(￣ˇ￣)/\n剧情大概讲的就是城市里最近出现了八尺女的传说~\n而且还是专门找小男孩的，男主听到之后，在回家的途中~看到了八尺女~\n还是看到八尺女在吃另外一位小男孩的场景~然后被八尺女发现了~\n随后八尺女跟着男主回家，在晚上，入侵了小男孩家~就发生了很幸福的事情了 QAQ 继续阅读 →\n					', '[桜都字幕组][メリー・ジェーン]都市伝説シリーズ 其の参 八尺さん[附前作]', '167', '4.25', '998C2BB11416A99FF546C8E1F0F4044505C5FD50');
