INSERT INTO `article` VALUES ('1854', '1480247537000', 'https://llss.pl/wp/all/anime/%e6%a1%9c%e9%83%bd%e5%ad%97%e5%b9%95%e7%bb%84%e9%88%b4%e6%9c%a8%e3%81%bf%e3%82%89%e4%b9%83%e9%a3%bc%e8%82%b2x%e5%bd%bc%e5%a5%b3-%e5%a4%a9%e4%bd%bf%e3%81%ae%e5%a4%89%e8%b2%8c-%e7%b7%a8/', 'https://ww3.sinaimg.cn/large/0060lm7Tgw1fa81ttfjd8j30uu0hcn1a.jpg', '桜都字幕组|鈴木みら乃', '\n			\n你们的傻东西又来了，~~太累了这段时间，一到月底，那么多新番堆积在一起~\n明知道上班很累啊！！！\n这番这么快就第三话了，这奇葩的剧情我真的没法看懂。反正就是名称为傻东西的男主。\n因为看上了女主，然后把女主带回家禁了，从一开始没发生任何事情，到现在发生了很多事情~\n嘿嘿嘿φ(≧ω≦*)♪\n一开始各种反抗，这话不就好了么~ 继续阅读 →\n					', '[桜都字幕组][鈴木みら乃]×彼女 天使の変貌編[附前作]', '55', '3.87', 'F5DF026160942C75BC8C04B6A4C65F80255D3302');
