INSERT INTO `article` VALUES ('1856', '1480939068000', 'https://llss.pl/wp/all/anime/%e6%a1%9c%e9%83%bd%e5%ad%97%e5%b9%95%e7%bb%84%e3%83%a1%e3%83%aa%e3%83%bc%e3%82%b8%e3%82%a7%e3%83%bc%e3%83%b3%e4%bb%8a%e3%81%8b%e3%82%89%e3%82%a2%e3%82%bf%e3%82%b7%e4%b8%8b/', 'https://ww4.sinaimg.cn/large/0060lm7Tgw1fag8uq7mfqj30uu0hcad6.jpg', 'メリージェーン|桜都字幕组', '\n			\n好像好久都没有NTR番了，终于有你们喜欢的NTR了~\n这番上一话开始我还以为是一个美好的兄妹番~没想到便太大叔老师变身牛头人进来搞事情！\n这话前半段基本都是牛头人老师不断地对女主下狠手，而且还弄了个房间专门搞事情的\n还把长啊！年轻的老师啊！全都带进来了，还隔着玻璃窗？当着哥哥面对妹妹下狠手\n最后老师都被，然后两兄妹也都退学了，为了过上和哥哥更美好的生活~妹妹对陌生人\n感兴趣了，内心已经喜欢上NTR了 /(ㄒoㄒ)/~~喵喵喵~ 继续阅读 →\n					', '[桜都字幕组][メリージェーン]今からアタシ……下巻 セカンドバージンも捧げます[附前作]', '76', '4.36', '03FE8208CB923C6159C1D23DDBE2A83A9D14595D');
