INSERT INTO `article` VALUES ('1905', '1490963849000', 'https://llss.pl/wp/all/anime/%e7%94%9f%e8%82%89%e3%81%88%e3%82%8d%e3%82%bc%e3%83%9f-%ef%bd%9e%e3%82%a8%e3%83%83%e3%83%81%e3%81%ab%e3%83%a4%e3%83%ab%e3%81%8d%e3%81%ababc%ef%bd%9e-the-animation%ef%bc%9c%e6%8a%bd%e9%81%b8%e3%81%a7/', 'https://llss.pl/wp/wp-content/uploads/2017/03/4-3.jpg', 'THE ANIMATION|学生|教育|老师|考试', '\n			\n楠国恭司——一位让无数学渣和问题都成功考入英学府的传说般的自由补习讲师。在这个夏天，他突然接到了一份委托。委托来自于业界第一的天神原补习，他们每年都要去度假酒店之岛进行只限女生的补习合宿，希望他能来当讲师。然而，他实施的指导方法其实是“通过H这一途径来将潜在能力和代谢激发出来实现质的飞跃”。在夏日的南岛，泛滥着H的指导开始了—— 继续阅读 →\n					', '【生】えろゼミ ～エッチにヤルきにABC～ THE ANIMATION＜抽選で1名様に声優サイン入り台本プレゼント＞', '40', '4.2', '9ECDD85F03E70674D39CD34E696DD95140ADD92D');
