INSERT INTO `article` VALUES ('1986', '1599494211000', 'https://llss.fan/wp/all/anime/%e5%bd%bc%e5%a5%b3%e3%81%8c%e3%83%a4%e3%83%84%e3%81%ab%e3%81%8b%e3%82%8c%e3%81%9f%e3%83%92-%ef%bd%9e%e6%96%b0%e5%a6%bb%e3%81%ae%e3%83%8f%e3%82%b8%e3%83%a1%e3%83%86%ef%bd%9e%e3%80%90p/', 'https://llss.fan/wp/wp-content/uploads/2020/08/021.jpg', 'NTR|NUR', '\n			夫妻在同一个公司上班，然后在公司玩的时候，被女主的同事看到了。女主怕事情曝光老公丢掉工作。于是只有对这个同事惟命是从 继续阅读 →\n					', '【熟肉】彼女がヤツにかれたヒ ～新妻のハジメテ……～', '36', '4.11', '673b90e345e9d0b95e38e4a8b1ab5b8906466b5c');
