INSERT INTO `article` VALUES ('2036', '1603724404000', 'https://llss.fan/wp/all/anime/infinite-vol-2%e6%b0%b4%e9%be%8d%e6%95%ac/', 'https://llss.fan/wp/wp-content/uploads/2020/10/02-12.jpg', 'Queen Bee|姐妹|水龍敬|碧池|逆后宫', '\n			这次动画是大师[水龍敬]的原作改编。剧情相当紧凑。一共两个个小故事，第一个讲母上和妹妹在家里，第二个讲大姐課長槍間繰子在公司里培训新员工的故事… 继续阅读 →\n					', '【熟肉】INFINITE Vol.2[水龍敬]【附前作】', '14', '3.29', '478eb44234013f803327bb13a5da897b126f26c4');
