INSERT INTO `article` VALUES ('2064', '1608318449000', 'https://llss.fan/wp/all/anime/%e3%80%90%e7%86%9f%e8%82%89%e3%80%91%e3%82%a8%e3%82%bf%e3%83%8b%e3%83%86%e3%82%a3-%ef%bd%9e%e6%b7%b1%e5%a4%9c%e3%81%ae%e6%bf%a1%e6%81%8b%e3%81%a1%e3%82%83%e3%82%93%e3%81%ad%e3%82%8b%e2%99%a1-9/', 'https://llss.fan/wp/wp-content/uploads/2020/12/04-3.jpg', '女性向|霸道总裁', '\n			女主名字叫深田咏美丸山里美，25岁，没有交过男朋友，就职于大型制衣公司【布兰贝尔蒂】，由于存在感比较薄弱的原因，经常莫名其妙的跌倒，被锁在仓库里等，然后霸道总裁来救她，众所周知，美女们站在丑男面前如大山一样稳健，在霸道总裁面前就很容易跌倒，这个是符合万有引力定律的，所以剧情看起来非常合理 继续阅读 →\n					', '【熟肉】エタニティ ～深夜の濡恋ちゃんねる♡～第9話', '0', '0', '2163D8AB0CDB3A1A15000AAD388A9D98941A0536');
